changeSet.sql:
-- Schema creation -- probs not very useful apart from development since any managed user will have had it setup separately CREATE SCHEMA IF NOT EXISTS mdah;
-- Images CREATE TABLE mdah.images ( filename text primary key, chapter_hash text not null, mode text not null, content_type text not null, size integer not null );
-- Index on chapter, since it's the best discriminating factor in our case CREATE UNIQUE INDEX idx_chapter_hash ON mdah.images (chapter_hash);
